import logo from './logo.svg';
import './App.css';
import { Component } from 'react';
import StocksList from './components/StocksList'
import {Container, Button} from 'react-bootstrap'
import AddStocks from './AddStocks';
class App extends Component{

    constructor(props) {
        super(props);
        this.state = {
          isAddProduct: false,
          error: null,
          response: {}
        }
        this.onFormSubmit = this.onFormSubmit.bind(this);
    }
    onCreate() {
        this.setState({ isAddProduct: true });

    }
    onFormSubmit(data) {
        const apiUrl = 'http://localhost:8080/api/stock';

        const myHeaders = new Headers();
        myHeaders.append('Content-Type', 'application/json');

        const options = {
          method: 'POST',
          body: JSON.stringify(data),
          myHeaders
        };

        fetch(apiUrl, options)
          .then(res => res.json())
          .then(result => {
            this.setState({
              response: result,
              isAddProduct: false
            })
          },
          (error) => {
            this.setState({ error });
          }
        )
    }
    render() {
      return (
        <div className="container-fluid">
            <Container>
                <nav>
                    <div className="nav-wrapper center-align">
                        <h1> Stocks Market </h1>
                    </div>
                </nav>

                <div className ="row" border="1">
                     {!this.state.isAddProduct && <Button variant="primary" onClick={() => this.onCreate()}>Add Product</Button>}
                      {this.state.response.status === 'success' && <div><br /><Alert variant="info">{this.state.response.message}</Alert></div>}
                      {!this.state.isAddProduct && <StocksList />}
                      {this.state.isAddProduct && <AddStocks onFormSubmit={this.onFormSubmit}/>}
                      {this.state.error && <div>Error: {this.state.error.message}</div>}
//                    <StocksList />
                </div>
            </Container>
        </div>
      );
  }
}

export default App;
