import logo from './logo.svg';
import './App.css';
import { Component } from 'react';
import StocksList from './components/StocksList'
import {Container, Button, Alert} from 'react-bootstrap'
import AddStocks from './components/AddStocks';
class App extends Component{

    constructor(props) {
        super(props);
        this.state = {
          isAddStock: false,
          error: null,
          response: {}
        }
        this.onFormSubmit = this.onFormSubmit.bind(this);
    }
    onCreate() {
        this.setState({ isAddStock: true });

    }

    onFormSubmit(data) {
            fetch('http://localhost:8080/api/stock', {
                method: "POST",
                headers:{
                    "content-type": "application/json",
                },
                body: JSON.stringify(data),
            })
            .then(res => res.json())
            .then(result => {
              this.setState({
                  response: result,
                  isAddStock: false
              })
              },
             (error) => {
              this.setState({ error });
             }
            )
        }
    render() {
      return (
        <div className="container-fluid">
            <Container>
                <nav>
                    <div className="nav-wrapper center-align">
                        <h1> Stocks Market </h1>
                    </div>
                </nav>

                <div className ="row">
                     {!this.state.isAddStock && <Button variant="primary" onClick={() => this.onCreate()}>Add Stocks</Button>}
                      {this.state.response.status === 'success' && <div><br /><Alert variant="info">{this.state.response.message}</Alert></div>}
                      {!this.state.isAddStock && <StocksList />}
                      {this.state.isAddStock && <AddStocks onFormSubmit={this.onFormSubmit}/>}
                      {this.state.error && <div>Error: {this.state.error.message}</div>}

                </div>
            </Container>
        </div>
      );
  }
}

export default App;
