import React, { Component } from 'react';
import { Row, Form, Col, Button } from 'react-bootstrap';
export default class AddStocks extends Component {

//    submitStock(event) {
//        event.preventDefault();
//        let stock = {
//            stockName: this.refs.stockName.value,
//            currentPrice: this.refs.currentPrice.value
//        }
//        fetch('http://localhost:8080/api/stock', {
//            method: "POST",
//            headers:{
//                "content-type": "application/json",
//            },
//            body: JSON.stringify(stock),
//        })
//        .then(response => response.json());
     constructor(props) {
         super(props);
         this.initialState = {
            id: '',
           stockName: '',
           currentPrice: ''
         }
         if(props.stock) {
            this.state = props.stock
         } else {
            this.state = props.initialState
         }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.onHandleNumberChange = this.onHandleNumberChange.bind(this);


    }
    handleChange(event) {
        const name = event.target.name;

        const value = event.target.value.replace(/[^A-Za-z]/ig, '')

        this.setState({
          [name]: value
        })
    }

    onHandleNumberChange = e => {
            const name = e.target.name;
            let price = e.target.value;

            if (!Number(price)) {
                return;
            }
            this.setState({
                [name]: price
            });
        };

    handleSubmit(event) {
        event.preventDefault();
        this.props.onFormSubmit(this.state);
        this.setState(this.initialState);
    }


    render() {
    let pageTitle;
                    if(this.state.id) {
                      pageTitle = <h2>Edit Stock</h2>
                    } else {
                      pageTitle = <h2>Add Stock</h2>
                    }
        return(
//             <div className="row">
//                <form className="col s12" onSubmit={this.submitStock.bind(this)}>
//                  <div className="row">
//                    <div className="input-field col s6">
//                      <input ref="stockName" type="text" className="validate"/>
//                      <label htmlFor="stockName">Stock Name</label>
//                    </div>
//                    <div className="input-field col s6">
//                      <input ref="currentPrice" type="number" className="validate"/>
//                      <label htmlFor="currentPrice">Current Price</label>
//                    </div>
//                  </div>
//
//
//                  <div className="row">
//                    <button className="waves-effect waves-light btn" type ="submit" name="action"> Add Stock </button>
//                  </div>
//                </form>
//             </div>

            <div>
                    <h2>{pageTitle}</h2>
                    <Row>
                      <Col sm={6}>
                        <Form onSubmit={this.handleSubmit}>
                          <Form.Group controlId="stockName">
                            <Form.Label>Stock Name</Form.Label>
                            <Form.Control
                              type="text"
                              name="stockName"
                              value={this.state.stockName}
                              onChange={this.handleChange}
                              placeholder="Stock Name"/>
                          </Form.Group>
                          <Form.Group controlId="currentPrice">
                            <Form.Label>Current Price</Form.Label>
                            <Form.Control
                              type="number"
                              name="currentPrice"
                              value={this.state.currentPrice}
                              onChange={this.onHandleNumberChange}
                              placeholder="Current Price" />
                          </Form.Group>

                          <Form.Group>
                            <Form.Control type="hidden" name="id" value={this.state.id}/>
                            <Button variant="success" type="submit">Save</Button>
                          </Form.Group>
                        </Form>
                      </Col>
                    </Row>
                  </div>
        )
    }
}
