import logo from './logo.svg';
import './App.css';
import { Component } from 'react';
import StocksList from './components/StocksList'
import {Container, Button, Alert} from 'react-bootstrap'
import AddStocks from './components/AddStocks';
class App extends Component{

    constructor(props) {
        super(props);
        this.state = {
          isAddStock: false,
          error: null,
          response: {},
          stock: {},
          isEditStock: false
        }
        this.onFormSubmit = this.onFormSubmit.bind(this);
    }
    onCreate() {
        this.setState({ isAddStock: true });

    }

    onFormSubmit(data) {

        let apiUrl;
        let options;
        const myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");
        myHeaders.append("Accept", "application/json");
        if(this.state.isEditStock){
          apiUrl = 'http://localhost:8080/api/stock';
          options = {
            method: 'PUT',
            body: JSON.stringify(data),
            myHeaders
          };
        } else {
          apiUrl = 'http://localhost:8080/api/stock';
          options = {
            method: 'POST',
            body: JSON.stringify(data),
            myHeaders
          };
        }
        fetch(apiUrl, options)
          .then(res => res.json())
          .then(result => {
            this.setState({
              response: result,
              isAddStock: false,
              isEditStock: false
            })
          },
          (error) => {
            this.setState({ error });
          }
          )
    }

    editStock = stockId => {

      const apiUrl = 'http://localhost:8080/api/stock/' + stockId;
      const myHeaders = new Headers();
      myHeaders.append('Content-Type', 'application/json');
      myHeaders.append('Accept', 'application/json');
        const options = {
          method: 'GET',
          myHeaders
        }

        fetch(apiUrl, options)
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                stock: result,
                isEditProduct: true,
                isAddProduct: true
              });
            },
            (error) => {
              this.setState({ error });
            }
          )
    }
    render() {
        let productForm;
        if(this.state.isAddStock || this.state.isEditStock) {
          productForm = <AddStocks onFormSubmit={this.onFormSubmit} stock={this.state.stock} />
        }
      return (
        <div className="container-fluid">
            <Container>
                <nav>
                    <div className="nav-wrapper center-align">
                        <h1> Stocks Market </h1>
                    </div>
                </nav>

                <div className ="row">
                     {!this.state.isAddStock && <Button variant="primary" onClick={() => this.onCreate()}>Add Stocks</Button>}
                      {this.state.response.status === 'success' && <div><br /><Alert variant="info">{this.state.response.message}</Alert></div>}
                      {!this.state.isAddStock && <StocksList editStock={this.editStock}/>}
                      { productForm }
                      {this.state.error && <div>Error: {this.state.error.message}</div>}

                </div>
            </Container>
        </div>
      );
  }
}

export default App;
