import React, { Component } from 'react';

export default class AddStocks extends Component {

    submitStock(event) {
        event.preventDefault();
        let stock = {
            stockName: this.refs.stockName.value,
            currentPrice: this.refs.currentPrice.value
        }
        fetch('http://localhost:8080/api/stock', {
            method: "POST",
            headers:{
                "content-type": "application/json",
            },
            body: JSON.stringify(stock),
        })
        .then(response => response.json());
        window.location.reload();
    }

    render() {
        return(
             <div class="row">
                <form class="col s12" onSubmit={this.submitStock.bind(this)}>
                  <div class="row">
                    <div class="input-field col s6">
                      <input placeholder="Placeholder" ref="stockName" type="text" class="validate"/>
                      <label htmlFor="stockName">First Name</label>
                    </div>
                    <div class="input-field col s6">
                      <input ref="currentPrice" type="text" class="validate"/>
                      <label htmlFor="currentPrice">Last Name</label>
                    </div>
                  </div>

                  <div class="row">
                    <div class="input-field col s12">
                      <input ref="current Price" type="email" class="validate"/>
                      <label htmlFor="email">Email</label>
                    </div>
                  </div>
                  <div class="row">
                    <button className="waves-effect waves-light btn" type ="submit" name="action"> Add Stock </button>
                  </div>
                </form>
             </div>
        )
    }
}
