    constructor(props) {
        super(props);
        this.state = {
          isAddStock: false,
          error: null,
          response: {},
          stock: {},
          isEditStock: false
        }
        this.onFormSubmit = this.onFormSubmit.bind(this);
    }
    onCreate() {
        this.setState({ isAddStock: true });

    }

    onFormSubmit(data) {
