              apiUrl = 'http://localhost:8080/api/stock/';
            } else {
              apiUrl = 'http://localhost:8080/api/stock';
            }

            const myHeaders = new Headers();
            myHeaders.append('Content-Type', 'application/json');

            const options = {
              method: 'POST',
              body: JSON.stringify(data),
              myHeaders
            };
         }
    editStock = stockId => {

        const apiUrl = 'http://localhost:8080/api/stock/' + {stockId};

        const myHeaders = new Headers();
          myHeaders.append('Content-Type', 'application/json');
          myHeaders.append('Accept', 'application/json');
        const options = {
          method: 'GET',
          myHeaders
        }

        fetch(apiUrl, options)
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                stock: result,
                isEditProduct: true,
                isAddProduct: true
              });
            },
            (error) => {
              this.setState({ error });
            }
          )
    }
    render() {
      return (
        <div className="container-fluid">
            <Container>
                <nav>
                    <div className="nav-wrapper center-align">
                        <h1> Stocks Market </h1>
                    </div>
                </nav>

                <div className ="row">
                     {!this.state.isAddStock && <Button variant="primary" onClick={() => this.onCreate()}>Add Stocks</Button>}
                      {this.state.response.status === 'success' && <div><br /><Alert variant="info">{this.state.response.message}</Alert></div>}
                      {!this.state.isAddStock && <StocksList editStock={this.editStock}/>}
                      {this.state.isAddStock && <AddStocks onFormSubmit={this.onFormSubmit}/>}
                      {this.state.error && <div>Error: {this.state.error.message}</div>}

                </div>
            </Container>
        </div>
      );
  }
}

export default App;
