import React, { Component } from 'react';
import {Container, Button} from 'react-bootstrap'
import {Table} from 'react-bootstrap';

export default class StocksList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            stocks: []
        };
    }
    componentDidMount() {
        fetch('http://localhost:8080/api/stocks', {
            method: "GET",
            headers:{
                "content-type": "application/json",
                "accept" : "*/*",
            },
        })
        .then(response=>response.json())
        .then(data =>{
            this.setState({
                stocks:data
                })
            },
            error=> {
                this.setState({
                    error:error
                })
            }

        )
    }
    render() {

        const {error, stocks} = this.state;
        if(error) {
            return (
                <div> Error:{error.message} </div>
            )
        } else {
            return (
                <div>
                    <h2> Stocks List </h2>
                    <Table>
                        <thead>
                            <tr>
                                <th> #ID</th>
                                <th> Stock Name</th>
                                <th> Current Price</th>
                                <th> Last Updated</th>
                                <th> Action </th>

                            </tr>
                        </thead>
                        <tbody>
                        {stocks.map( stock=> (
                            <tr key={stock.id}>
                                <td> {stock.id}</td>
                                <td> {stock.stockName}</td>
                                <td> {stock.currentPrice}</td>
                                <td> {stock.lastUpdate}</td>
                                <td> <Button variant="info" onClick={() => this.props.editStock(stock.id)}> Edit </Button> </td>
                            </tr>
                        ))}
                        </tbody>
                    </Table>
                </div>
            )
        }


    }
}
